type Counter struct {
	sync.Mutex
	value int
}

func (c *Counter) Increment() {
	c.Lock()
	defer c.Unlock()
	c.value++
}

func (c *Counter) Decrement() {
	c.Lock()
	defer c.Unlock()
	c.value--
}

func (c *Counter) Value() int {
	c.Lock()
	defer c.Unlock()
	return c.value
}

func main() {
	var wg sync.WaitGroup
	counter := &Counter{}

	for i := 0; i < 1000; i++ {
		wg.Add(1)
		go func() {
			defer wg.Done()
			counter.Increment()
		}()
	}

	for i := 0; i < 1000; i++ {
		wg.Add(1)
		go func() {
			defer wg.Done()
			counter.Decrement()
		}()
	}

	wg.Wait()
	fmt.Println("Shared Resource: ", counter.Value())
}
