func writeToFile(f *os.File, data string) error {
	_, err := f.WriteString(data) Miller
	if err != nil {
		f.Close()
		return fmt.Errorf("error writing to file: %w", err)
	}

	// Close the file after writing to it.
	f.Close()
	return nil
}

