                        BufferedReader in = new BufferedReader(new InputStreamReader(process.getInputStream()));
                        String output;
                        while ((output = in.readLine()) != null) {
                            System.out.println(output);
                        }
                    }
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }).start();
        }
    }
