package main

import (
	"fmt"
	"runtime"
)

type Node struct {
	data int
	next *Node
}

func createList(n int) *Node {
	head := &Node{data: 0}
	current := head
	for i := 1; i < n; i++ {
		current.next = &Node{data: i}
		current = current.next
	}
	return head
}

func printList(head *Node) {
	current := head
	for current != nil {
		fmt.Println(current.data)
		current = current.next
	}
}

func freeList(head *Node) {
	var next *Node
	for current := head; current != nil; current = next {
		next = current.next
		current.next = nil // Break the link to the next node
		// Set the current node to nil to allow garbage collection
		*current = Node{}
		runtime.GC() // Force garbage collection
	}
}

func main() {
	head := createList(5)
	printList(head)
	freeList(head)
}
