import Foundation

class UnsafePointerExample {
    private var data: Data?
    private var pointer: UnsafeMutableRawPointer?

    init(size: Int) {
        data = Data(repeating: 0, count: size)
        pointer = data?.baseAddress?.assumingMemoryBound(to: UInt8.self)
