        ptr.advanced(by: offset).storeBytes(of: value, as: UInt8.self)
    }

    func readFromPointer(atOffset offset: Int) -> UInt8? {
        guard let ptr = pointer else { return nil }
        return ptr.load(fromByteOffset: offset, as: UInt8.self)
    }
