	s.RLock()
	defer s.RUnlock()
	value, ok := s.data[key]
	return value, ok
}

func (s *SafeMap) Append(key, value string) {
	s.Lock()
	defer s.Unlock()
	s.data[key] = append(s.data[key], value)
}

func main() {
	m := NewSafeMap()

	var wg sync.WaitGroup
	for i := 0; i < 10; i++ {
		wg.Add(1)
		go func(i int) {
			defer wg.Done()
			m.Append("key", fmt.Sprintf("value%d", i))
		}(i)
	}

	wg.Wait()

	values, _ := m.Read("key")
